<?php /*Template Name: Careers */get_header();?><div class="wrapper">	<div class="full bgcolor-001F42 triangle-bottom-lightblue">		<div class="content">			<div class="page-title-wrapper">				<h1 class="page-title center-title"><?php the_field( 'hero_headline' ); ?></h1>				<div class="sub-text-wrapper">				</div>			</div> <!-- class-page-title-wrapper  -->		</div><!--content-->	</div> <!--full-->	<div class="full">		<div class="content">			<div class="section-careers-wrapper">				<div class="section-career-header-text">					<h2><?php the_field( '_introduction_headline' ); ?></h2>					<?php the_field( 'introduction_body_copy' ); ?>				</div> <!-- section-career-header-text -->				<div class="section-career-sidebar">					<h2>Open <br>Positions</h2>				</div> <!-- section-career-sidebar -->				<div class="section-career-jobs-group">					<?php					$jobs = get_google_hire_jobs();					$jobs_categories = [];					foreach($jobs as $job)					{						$department = $job['hiringOrganization']['department']['name'];						$location = $job['jobLocation']['address']['addressLocality'] . ', ' . $job['jobLocation']['address']['addressRegion'];						$jobs_categories[$department][] = array(							'title'		=> $job['title'],							'url'		=> $job['url'],							'location'	=> $location						);					}					ksort($jobs_categories);					?>					<?php foreach($jobs_categories as $key => $jobs_category): ?>						<div class="section-career-group-wrapper">							<h2><?php echo $key; ?></h2>							<?php foreach($jobs_category as $job): ?>								<a class="job" href="<?php echo $job['url']; ?>" target="_blank">									<p><b><?php echo $job['title']; ?></b></p>									<p><?php echo $job['location']; ?></p>								</a>							<?php endforeach; ?>						</div> <!-- section-career-group-wrapper -->					<?php endforeach; ?>				</div>  <!-- section-career-jobs-wrapper -->			</div> <!-- section-careers-wrapper -->		</div><!--content-->	</div> <!--full-->	<!-- 